import React, { useState, useEffect } from 'react';
import BubbleCalendar from './BubbleCalendar';

const ConfirmationModal = ({ isOpen, onClose, selectedServices, onConfirmReservation }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('cash'); // Default payment method
  const [showBankDetails, setShowBankDetails] = useState(false);

  if (!isOpen) return null;

  const total = selectedServices.reduce((sum, service) => {
    const price = parseFloat(service.price.replace('.', '').replace(' Gs', ''));
    return sum + price;
  }, 0);

  const handleDateTimeSelection = (date, time) => {
    setSelectedDate(date);
    setSelectedTime(time);
  };

  const handlePayAndConfirm = () => {
    if (!selectedDate || !selectedTime) {
      alert('Por favor, selecciona una fecha y hora para tu reserva.');
      return;
    }
    if (!paymentMethod) {
      alert('Por favor, selecciona un método de pago.');
      return;
    }

    const reservationDetails = {
      services: selectedServices,
      date: selectedDate.toLocaleDateString('es-ES'),
      time: selectedTime,
      total: total,
      paymentMethod: paymentMethod,
    };

    if (paymentMethod === 'cash') {
      const message = `¡Hola! Quiero reservar los siguientes servicios para el ${reservationDetails.date} a las ${reservationDetails.time}:\n\n${selectedServices.map(s => `- ${s.name} (${s.price})`).join('\n')}\n\nTotal: ${total.toLocaleString('es-PY')} Gs.\n\n¡Espero tu confirmación!`;
      const whatsappUrl = `https://wa.me/595994116266?text=${encodeURIComponent(message)}`;
      window.open(whatsappUrl, '_blank');
      onConfirmReservation(reservationDetails);
      onClose();
    } else if (paymentMethod === 'card') {
      setShowBankDetails(true);
    }
  };

  const handleCloseBankDetails = () => {
    setShowBankDetails(false);
    onConfirmReservation({
      services: selectedServices,
      date: selectedDate.toLocaleDateString('es-ES'),
      time: selectedTime,
      total: total,
      paymentMethod: paymentMethod,
    });
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md transform transition-all duration-300 scale-100 opacity-100">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900">Confirmar Reserva</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700 text-3xl leading-none">
            &times;
          </button>
        </div>

        <h3 className="text-xl font-semibold text-gray-800 mb-4">Servicios Seleccionados:</h3>
        <ul className="space-y-2 mb-6 max-h-40 overflow-y-auto border-b pb-4">
          {selectedServices.map((service, index) => (
            <li key={index} className="flex justify-between items-center">
              <span className="text-gray-700">{service.name}</span>
              <span className="font-semibold text-gray-800">{service.price}</span>
            </li>
          ))}
        </ul>

        <div className="mb-6">
          <h4 className="block text-gray-700 text-sm font-bold mb-2">
            Selecciona Fecha y Hora:
          </h4>
          <BubbleCalendar onSelectDateTime={handleDateTimeSelection} />
          {selectedDate && selectedTime && (
            <p className="mt-4 text-center text-lg font-semibold text-gray-800">
              Has seleccionado: {selectedDate.toLocaleDateString('es-ES')} a las {selectedTime}
            </p>
          )}
        </div>

        <div className="mb-6">
          <h4 className="block text-gray-700 text-sm font-bold mb-2">
            Método de Pago:
          </h4>
          <div className="flex space-x-4">
            <label className="inline-flex items-center">
              <input
                type="radio"
                className="form-radio text-black"
                name="paymentMethod"
                value="cash"
                checked={paymentMethod === 'cash'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <span className="ml-2 text-gray-700">Efectivo</span>
            </label>
            <label className="inline-flex items-center">
              <input
                type="radio"
                className="form-radio text-black"
                name="paymentMethod"
                value="card"
                checked={paymentMethod === 'card'}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              <span className="ml-2 text-gray-700">Tarjeta (Transferencia)</span>
            </label>
          </div>
        </div>

        <div className="flex justify-between items-center border-t pt-4 mt-4">
          <span className="text-xl font-bold text-gray-900">Total a Pagar:</span>
          <span className="text-2xl font-extrabold text-black">{total.toLocaleString('es-PY')} Gs</span>
        </div>

        <button
          onClick={handlePayAndConfirm}
          className="w-full bg-black text-white py-3 rounded-lg mt-6 font-semibold hover:bg-gray-800 transition-colors duration-300"
        >
          Pagar y Confirmar
        </button>
      </div>

      {showBankDetails && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-sm transform transition-all duration-300 scale-100 opacity-100">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">Datos Bancarios</h2>
              <button onClick={handleCloseBankDetails} className="text-gray-500 hover:text-gray-700 text-3xl leading-none">
                &times;
              </button>
            </div>
            <p className="text-gray-700 mb-4">Por favor, realiza la transferencia a los siguientes datos:</p>
            <div className="bg-gray-100 p-4 rounded-lg mb-6">
              <p className="font-semibold text-gray-800">Alias:</p>
              <p className="text-black text-lg font-bold mb-2">6781184</p>
              <p className="font-semibold text-gray-800">Banco:</p>
              <p className="text-black text-lg font-bold">Banco Kiki LBK (Ejemplo)</p>
            </div>
            <p className="text-gray-600 text-sm text-center">Una vez realizada la transferencia, envíanos el comprobante por WhatsApp para confirmar tu reserva.</p>
            <button
              onClick={handleCloseBankDetails}
              className="w-full bg-black text-white py-3 rounded-lg mt-6 font-semibold hover:bg-gray-800 transition-colors duration-300"
            >
              Entendido
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ConfirmationModal;

