import React from 'react';
import './styles.css'; // Asegúrate de importar tu archivo CSS

const HeroSection = () => {
  return (
    <section className="hero-section">
      {/* Fondo geométrico moderno */}
      <div className="hero-background">
        <div className="hero-gradient"></div>
        <div className="hero-pattern"></div>
      </div>

      {/* Contenido principal */}
      <div className="hero-content">
        {/* Logo/texto con estilo moderno */}
        <div className="hero-logo">
          <h1>LBK</h1>
          <div className="hero-divider"></div>
          <p className="hero-subtitle">BARBERÍA</p>
          <p className="hero-year">DESDE 2021</p>
        </div>

        {/* Eslogan */}
        <p className="hero-slogan">
          <span>ESTILO PRECISO</span> PARA EL HOMBRE CONTEMPORÁNEO
        </p>
      </div>

      {/* Elemento "Deslizar" moderno */}
      <div className="hero-scroll-indicator">
        <div className="scroll-circle">
          <div className="scroll-dot"></div>
        </div>
        <span className="scroll-text">DESLIZAR</span>
      </div>

      {/* Elementos decorativos modernos */}
      <div className="hero-decorations">
        {/* Líneas geométricas */}
        <div className="decoration-line top-line"></div>
        <div className="decoration-line right-line"></div>
        <div className="decoration-line bottom-line"></div>

        {/* Icono de tijeras */}
        <svg className="scissors-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1">
          <path d="M5.5 16.5L19.5 2.5M19.5 16.5L5.5 2.5" strokeLinecap="round"/>
        </svg>
      </div>
    </section>
  );
};

export default HeroSection;
