import React from 'react';

const HeroSection = () => {
  return (
    <section className="relative h-screen bg-cover bg-center" style={{ backgroundImage: "url('https://images.unsplash.com/photo-1621609782122-bc123221221c?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')" }}>
      <div className="absolute inset-0 bg-black opacity-60"></div>
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-white text-center px-4">
        <h1 className="text-5xl md:text-7xl font-bold mb-4 drop-shadow-lg">LBK</h1>
        <p className="text-xl md:text-2xl mb-8 max-w-2xl drop-shadow-lg">
          Donde el estilo y la tradici√≥n se encuentran para el caballero moderno.
        </p>
        <button className="bg-white text-black px-8 py-3 rounded-full text-lg font-semibold hover:bg-gray-200 transition-all duration-300 shadow-lg">
          Agenda tu cita
        </button>
      </div>
    </section>
  );
};

export default HeroSection;
